import { v4 } from "uuid";
export const initialState = {
  alert: [],
};
// {id: '', type: '', msg: ''}

export const reducer = (state, action) => {
  const { type, payload } = action;
  switch (type) {
    case "SET_ALERT":
      return {
        ...state,
        alert: { id: v4, type: payload.type, msg: payload.msg },
      };
  }
};
