import { v4 } from "uuid";
export const initialState = {
  alerts: [],
};
// {id: '', type: '', msg: ''}

export const reducer = (state, action) => {
  const { type, payload } = action;
  switch (type) {
    case "SET_ALERT":
      let newAlerts = state.alerts;
      newAlerts.push({ id: v4, type: payload.type, msg: payload.msg });
      return {
        ...state,
        alerts: newAlerts,
      };
    default:
      return state;
  }
};
