import React from "react";
import { useGlobalContext } from "../../context/context";

const Posts = () => {
  const { state, getPosts } = useGlobalContext();
  const { posts, loading } = state;
  React.useEffect(() => getPosts(), []);
  return posts;
};

export default Posts;
